import pygame, sysfrom pygame.math import Vector2class FRUIT :    def __init__(self):        self.x = 10        self.y = 10        self.pos = Vector2(self.x, self.y)            def draw_fruit(self):        fruit_rect = pygame.Rect(self.pos.x * cell_size, self.pos.y * cell_size, cell_size, cell_size)        pygame.draw.rect(screen, (255,0,0), fruit_rect)        pygame.init()cell_size = 40cell_num = 20screen = pygame.display.set_mode((cell_size * cell_num ,cell_size * cell_num))clock = pygame.time.Clock()fruit = FRUIT()while True :        for event in pygame.event.get():        if event == pygame.QUIT :            pygame.quit()            sys.exit()                screen.fill((0,255,0))    fruit.draw_fruit()        pygame.display.update()    clock.tick(60)